var Big = require('big.js');

function newBigInt(){
    var operNum = 10000000;
    var startTime = process.hrtime();
    for(var i=0;i<operNum;i++){
        new Big(2**54+1)
    }
    var diff = process.hrtime(startTime);
    var cost = diff[0]*1000+diff[1]/1000000;
    console.log(`big.js ${operNum} oper, cost:${cost}`)
    
    startTime = process.hrtime();
    for(var i=0;i<operNum;i++){
        BigInt(2**54+1)
    }
    
    var diff = process.hrtime(startTime);
    var cost = diff[0]*1000+diff[1]/1000000;
    console.log(`BigInt ${operNum} oper, cost:${cost}`)
}

function operator(){
    var operNum = 10000000;
    var bigJs1 = new Big(2**54+1), bigJs2 = new Big(2);
    var bigInt1 = BigInt(2**54+1), bigInt2 = BigInt(2)
    var startTime = process.hrtime();
    for(var i=0;i<operNum;i++){
        bigJs1.plus(bigJs2)
    }
    var diff = process.hrtime(startTime);
    var cost = diff[0]*1000+diff[1]/1000000;
    console.log(`big.js ${operNum} oper, cost:${cost}`)
    
    startTime = process.hrtime();
    for(var i=0;i<operNum;i++){
        bigInt1 + bigInt2
    }
    
    var diff = process.hrtime(startTime);
    var cost = diff[0]*1000+diff[1]/1000000;
    console.log(`BigInt ${operNum} oper, cost:${cost}`)
}

operator();