var TafStream = require("../../../");

var os = new TafStream.JceOutputStream();
os.writeFloat(0, 1000.234);         //C++显示1000.234的值是：(04) 44 7a 0e fa
os.writeDouble(1, 1000.23473332);   //C++显示1000.23473332的值是：(05) 40 8f 41 e0 bb dc e5 74

os.getBinBuffer().print();

var stream = os.getBinBuffer();
stream.position = 0;

var is = new TafStream.JceInputStream(stream);
var a  = is.readFloat(0, true);
var b  = is.readDouble(1, true);

console.log(a, b);
