var TafStream = require("../../../");


var str = new Buffer(20);
str.writeInt32BE(0x123456, 0);
str.writeInt32BE(0x223456, 4);

var os  = new TafStream.JceOutputStream();
os.writeString(0, str, true);

os.getBinBuffer().print();

var is  = new TafStream.JceInputStream(os.getBinBuffer());
str = is.readString(0, true, "", true);

console.log(str);
