const num=90000000;
let starttime=process.uptime();
for(let i=0;i<num;i++){
    new Buffer(50);
}
let endtime=process.uptime();
console.log("cost time :",endtime-starttime);

starttime=process.uptime();
for(let i=0;i<num;i++){
    Buffer.allocUnsafe(50);
}
endtime=process.uptime();
console.log("cost time :",endtime-starttime);