var TafStream = require("../../../");

//写入数据
var os = new TafStream.UniAttribute();
os.writeBoolean("nameBoolean", true);
os.writeInt8("nameInt8", 0x22);

//解码数据
var binBuffer = os.encode();
//binBuffer.print();

var is = new TafStream.UniAttribute();
is.decode(binBuffer);

//读出数据
var nameBoolean = is.readBoolean("nameBoolean");
console.log("nameBoolean:", nameBoolean);

var nameInt8 = is.readInt8("nameInt81");
console.log("nameInt8:", nameInt8);
