var TafStream = require("../../libjce");

//����MapǶ��
var map = new TafStream.Map(TafStream.Int32, TafStream.String);

map.insert(2, "3333");
map.insert(3, "4444");

var os  = new TafStream.JceOutputStream();
os.writeMap(0, map);
os.getBinBuffer().print();

var is  = new TafStream.JceInputStream(os.getBinBuffer());
map = is.readMap(0, true, TafStream.Map(TafStream.Int32, TafStream.String));

map.forEach(function (key, value){
    console.log(typeof  key, value);
});
