var Taf = require("../../libjce");

var wup_encode = new Taf.Wup();
wup_encode.writeUInt32("uin",  155069599);
wup_encode.writeString("name", "KevinTian");
wup_encode.writeUInt32("gid",  1002121);
wup_encode.writeUInt32("num",  10);

var BinBuffer = wup_encode.encode(true);

var data = BinBuffer.toNodeBuffer();

var wup_decode = new Taf.Wup();
wup_decode.decode(new Taf.BinBuffer(data));

var num  = wup_decode.readUInt32("num");
var gid  = wup_decode.readUInt32("gid");
var name = wup_decode.readString("name");
var uin  = wup_decode.readUInt32("uin");

console.log("name:", name);
console.log("num :", num);
console.log("gid :", gid);
console.log("uin :", uin);
